import React, { useState } from 'react';

type PackageInfo = {
  name: string;
  items: string[];
};

type Packages = {
  [key: string]: PackageInfo;
};

type PricingTerm = {
  [key: string]: number;
};

type Pricing = {
  [key: number]: PricingTerm;
};

const packages: Packages = {
  A: { name: 'Flexible', items: ['Flexible Terms', 'Enterprise Network Connectivity', 'Day 1 Branding & Signage', 'Core Occupancy Data'] },
  B: { name: 'Inclusive', items: ['Furnished', 'Full Digital Infrastructure', 'AV Services Hardware', 'Plant Programming', 'Food & Beverage Service'] },
  C: { name: 'Fully Managed', items: ['Dedicated WX Coordinator', 'Full Mail and Package Handling', 'Hardware & Data Collection', 'IT/AV Help Desk'] }
};

const pricing: Pricing = {
  2: { A: 100000, B: 120000, C: 140000 },
  3: { A: 80000, B: 100000, C: 120000 },
  4: { A: 60000, B: 80000, C: 100000 },
  5: { A: 60000, B: 80000, C: 100000 }
};

const App: React.FC = () => {
  const [selectedPackage, setSelectedPackage] = useState<string | null>(null);
  const [selectedTerm, setSelectedTerm] = useState<number | null>(null);

  const handleSelection = (pkg: string, term: number) => {
    setSelectedPackage(pkg);
    setSelectedTerm(term);
  };

  return (
    <div className="flex p-4">
      <div className="w-2/3">
        <h2 className="text-2xl font-bold mb-4">Service Package</h2>
        <div className="grid grid-cols-3 gap-4 mb-4">
          {Object.entries(packages).map(([key, value]) => (
            <div key={key} className="p-4 border rounded shadow">
              <h3 className="text-xl font-semibold">{key}</h3>
              <p>{value.name}</p>
            </div>
          ))}
        </div>
        <div className="grid grid-cols-4 gap-4">
          <div className="font-bold">Term</div>
          {Object.keys(packages).map((key) => (
            <div key={key} className="font-bold text-center">{key}</div>
          ))}
          {Object.entries(pricing).map(([term, prices]) => (
            <React.Fragment key={term}>
              <div className="font-bold">Year {term}</div>
              {Object.entries(prices).map(([pkg, price]) => (
                <button
                  key={`${term}-${pkg}`}
                  className={`p-2 border ${selectedPackage === pkg && selectedTerm === parseInt(term) ? 'bg-red-500 text-white' : 'hover:bg-red-100'}`}
                  onClick={() => handleSelection(pkg, parseInt(term))}
                >
                  ${price / 1000}k
                </button>
              ))}
            </React.Fragment>
          ))}
        </div>
      </div>
      <div className="w-1/3 pl-4">
        <Sidebar selectedPackage={selectedPackage} selectedTerm={selectedTerm} />
      </div>
    </div>
  );
};

interface SidebarProps {
  selectedPackage: string | null;
  selectedTerm: number | null;
}

const Sidebar: React.FC<SidebarProps> = ({ selectedPackage, selectedTerm }) => {
  return (
    <div className="border rounded shadow p-4">
      <h2 className="text-2xl font-bold mb-4">Details</h2>
      {selectedPackage && selectedTerm ? (
        <>
          <p>Package: {selectedPackage}</p>
          <p>Term: {selectedTerm} years</p>
          <p>Price: ${pricing[selectedTerm][selectedPackage].toLocaleString()}</p>
          {/* Add more details and calculations here */}
        </>
      ) : (
        <p>Select a package and term to see details</p>
      )}
    </div>
  );
};

export default App;
