import { Package, ManagementCosts } from './data';

const mockCalculateNER = (
  pkg: string, 
  term: number, 
  discountRate: number,
  managementCosts: ManagementCosts,
  packages: { [key: string]: Package }
): number => {
  // Base NER calculation
  let baseNER = 60; // Starting point for NER

  // Adjust NER based on term
  baseNER += (5 - term) * 1; // Slight increase for shorter terms

  // Adjust NER based on package
  if (pkg === 'B') {
    baseNER *= 1.10; // 10% higher for Package B
  } else if (pkg === 'C') {
    baseNER *= 1.20; // 20% higher for Package C
  }

  // Adjust NER based on term premium (discountRate)
  // Increase NER by $2 for each percentage point of the discount rate
  baseNER += discountRate * 2;



  // Ensure NER stays within a reasonable range ($50-$120)
  return Math.min(120, Math.max(50, baseNER));
};

export default mockCalculateNER;
