import React, { useState } from 'react';
import { packages, pricing, RSF, calculateMonthlyTotal, calculateAnnualTotal, getAdjustedValue, formatPrice } from './data';

type Package = {
  name: string;
  description: string;
  items: string[];
  params: {
    [key: string]: number;
  };
};

const FlipCard: React.FC<{ packageKey: string; packageData: Package }> = ({ packageKey, packageData }) => {
  const [isHovered, setIsHovered] = useState(false);

  return (
    <div 
      className="box-standard relative border-4 border-[#A82229] flex items-center justify-center"
      onMouseEnter={() => setIsHovered(true)}
      onMouseLeave={() => setIsHovered(false)}
    >
      <div className={`w-full h-full transition-opacity duration-300 ${isHovered ? 'opacity-0' : 'opacity-100'} flex flex-col items-center justify-center`}>
        <h3 className="text-7xl font-bold">{packageKey}</h3>
        <p className="text-lg">{packageData.name}</p>
      </div>
      <div className={`absolute top-0 left-0 w-full h-full bg-white p-1 overflow-auto transition-opacity duration-300 ${isHovered ? 'opacity-100' : 'opacity-0'}`}>
        <ul className="text-left text-sm leading-tight">
          {packageData.items.map((item, index) => (
            <li key={index} className="mb-1">{item}</li>
          ))}
        </ul>
      </div>
    </div>
  );
};

const PricingMatrix: React.FC<{ 
  selectedBox: {pkg: string, term: number} | null, 
  setSelectedBox: (box: {pkg: string, term: number} | null) => void,
  gapSize: number
}> = ({ selectedBox, setSelectedBox, gapSize }) => {
  const isSelected = (pkg: string, term: number) => {
    return selectedBox?.pkg === pkg && selectedBox.term === term;
  };

  const getOpacity = (term: number) => {
    switch(term) {
      case 2: return 'opacity-50';
      case 3: return 'opacity-80';
      case 4: return 'opacity-100';
      case 5: return 'opacity-25';
      default: return 'opacity-100';
    }
  };

  return (
    <div className="grid grid-cols-[auto,1fr,1fr,1fr] gap-y-4" style={{
      columnGap: `${gapSize}px`,
      gridTemplateColumns: `auto 1fr ${gapSize}px 1fr ${gapSize}px 1fr`
    }}>
      <div className="invisible"></div>
      {Object.entries(packages).map(([key, value]) => (
        <FlipCard key={key} packageKey={key} packageData={value} />
      ))}
      {Object.entries(pricing).map(([term, prices]) => (
        <React.Fragment key={term}>
          <div className={`box-standard flex justify-center items-center border-4 border-[#A82229] font-bold ${term === '5' ? 'opacity-25' : ''}`}>
            <div className="flex flex-col items-start">
              <div className="text-sm">Year</div>
              <div className="text-6xl mb-1">{term.padStart(2, '0')}</div>
            </div>
          </div>
          {Object.keys(prices).map((pkg) => {
            const monthlyTotal = calculateMonthlyTotal(pkg, parseInt(term));
            return (
              <button
                key={`${term}-${pkg}`}
                className={`box-standard flex items-center justify-center text-center
                  bg-[#A82229] text-white text-2xl font-bold
                  ${isSelected(pkg, parseInt(term)) ? 'bg-white !text-[#A82229] border-8 border-[#A82229]' : `border-4 border-[#A82229] ${getOpacity(parseInt(term))}`}
                  transition-all duration-300`}
                onClick={() => setSelectedBox({pkg, term: parseInt(term)})}
              >
                <div className={`${isSelected(pkg, parseInt(term)) ? 'transform scale-[1.8]' : ''}`}>
                  <span className="text-lg">$</span>
                  <span className="text-3xl">{formatPrice(monthlyTotal).slice(1, -1)}</span>
                  <span className="text-lg">k</span>
                </div>
              </button>
            );
          })}
        </React.Fragment>
      ))}
    </div>
  );
};

const Sidebar: React.FC<{ selectedBox: {pkg: string, term: number} | null }> = ({ selectedBox }) => {
  // ... (Sidebar component code remains unchanged)
};

const App: React.FC = () => {
  const [selectedBox, setSelectedBox] = useState<{pkg: string, term: number} | null>(null);
  const [gapSize, setGapSize] = useState<number>(8);

  return (
    <div className="p-8 font-['Inter'] font-black relative min-h-screen" style={{ color: '#A82229' }}>
      <h1 className="text-3xl mb-12 text-left">Real Time Pricing Matrix</h1>
      <div className="flex justify-between">
        <div style={{ width: '800px' }}>
          <PricingMatrix selectedBox={selectedBox} setSelectedBox={setSelectedBox} gapSize={gapSize} />
        </div>
        <div className="w-4" /> {/* Spacer */}
        <div style={{ width: '400px' }}>
          <Sidebar selectedBox={selectedBox} />
        </div>
      </div>
      <div className="mt-8">
        <label htmlFor="gap-slider" className="block mb-2">Adjust Column Gap:</label>
        <input
          id="gap-slider"
          type="range"
          min="0"
          max="100"
          value={gapSize}
          onChange={(e) => setGapSize(Number(e.target.value))}
          className="w-full"
        />
        <div className="text-center mt-2">{gapSize}px</div>
      </div>
    </div>
  );
};

export default App;
