import { Package, ManagementCosts } from './data';

const mockCalculateNER = (
  pkg: string, 
  term: number, 
  discountRate: number,
  managementCosts: ManagementCosts,
  packages: { [key: string]: Package }
): number => {
  // Base NER calculation
  let baseNER = 65; // Starting point for NER

  // Adjust NER based on term
  baseNER += (5 - term) * 2; // Increase NER for shorter terms

  // Adjust NER based on package
  if (pkg === 'B') {
    baseNER *= 1.15; // 15% higher for Package B
  } else if (pkg === 'C') {
    baseNER *= 1.30; // 30% higher for Package C
  }

  // Adjust NER based on term premium (discountRate)
  const premiumAdjustment = 1 + (discountRate / 100);
  baseNER *= premiumAdjustment;

  // Add some randomness (±$2)
  const randomFactor = (Math.random() - 0.5) * 4;
  baseNER += randomFactor;

  // Ensure NER stays within a reasonable range ($50-$100)
  return Math.min(Math.max(baseNER, 50), 100);
};

export default mockCalculateNER;
