import { Package } from './data';

const mockCalculateNER = (pkg: string, term: number, discountRate: number, managementCosts: any, packages: { [key: string]: Package }): number => {
  // Base NER calculation
  let baseNER = 70 - (term * 5); // Starts at $70 for 2-year term, decreases by $5 for each year

  // Adjust NER based on package
  if (pkg === 'B') {
    baseNER *= 1.15; // 15% higher for Package B
  } else if (pkg === 'C') {
    baseNER *= 1.30; // 30% higher for Package C
  }

  // Add some randomness (±$2)
  const randomFactor = (Math.random() - 0.5) * 4;

  // Ensure NER stays within $50-$70 range
  return Math.min(Math.max(baseNER + randomFactor, 50), 70);
};

export default mockCalculateNER;
