import React, { useState, useEffect, useMemo } from 'react';
import { managementCosts as initialManagementCosts, RSF, calculateMonthlyTotal } from './data';
import { PricingMatrix, Sidebar } from './PricingComponents';
import calculateNER from './calculateNER';

interface PricingData {
  [key: string]: {
    [key: string]: number;
  };
}

const App: React.FC = () => {
  const [selectedBox, setSelectedBox] = useState<{pkg: string, term: number} | null>(null);
  const [isExpanded, setIsExpanded] = useState(false);
  const [isManagementMode, setIsManagementMode] = useState(false);
  const [managementCosts, setManagementCosts] = useState(initialManagementCosts);
  const [discountRate, setDiscountRate] = useState(0.08); // 8% discount rate

  const nerData: PricingData = useMemo(() => ({
    2: { A: calculateNER('A', 2, discountRate), B: calculateNER('B', 2, discountRate), C: calculateNER('C', 2, discountRate) },
    3: { A: calculateNER('A', 3, discountRate), B: calculateNER('B', 3, discountRate), C: calculateNER('C', 3, discountRate) },
    4: { A: calculateNER('A', 4, discountRate), B: calculateNER('B', 4, discountRate), C: calculateNER('C', 4, discountRate) },
    5: { A: calculateNER('A', 5, discountRate), B: calculateNER('B', 5, discountRate), C: calculateNER('C', 5, discountRate) },
  }), [discountRate]);

  const getMatrixValue = (pkg: string, term: number): number => {
    if (isManagementMode) {
      return nerData[term][pkg];
    } else {
      return calculateMonthlyTotal(pkg, term);
    }
  };

  return (
    <div className="p-8 font-['Inter'] font-black relative min-h-screen" style={{ color: '#A82229' }}>
      <h1 className="text-2xl mb-20 text-left">Flex Office Pricing Matrix (v.01)</h1>
      <div className="flex justify-between">
        <div className="w-[800px]">
          <PricingMatrix 
            selectedBox={selectedBox} 
            setSelectedBox={setSelectedBox} 
            isManagementMode={isManagementMode}
            getMatrixValue={getMatrixValue}
            nerData={nerData}
          />
        </div>
        <div className="w-[450px] flex flex-col">
          <Sidebar 
            selectedBox={selectedBox} 
            setSelectedBox={setSelectedBox}
            isExpanded={isExpanded} 
            setIsExpanded={setIsExpanded}
            isManagementMode={isManagementMode}
            setIsManagementMode={setIsManagementMode}
            managementCosts={managementCosts}
            setManagementCosts={setManagementCosts}
            calculateNER={getNER}
            calculateTotal={calculateTotal}
            calculateMonthlyTotal={calculateMonthlyTotal}
            discountRate={discountRate}
          />
        </div>
      </div>
    </div>
  );
};

export default App;
