import React, { useState, useEffect, useCallback } from 'react';
import { managementCosts as initialManagementCosts, RSF, ManagementCosts } from './data';
import { PricingMatrix, Sidebar } from './PricingComponents';
import calculateNER from './calculateNER';
import { updatePricingMatrix } from './pricingCalculations';

const App: React.FC = () => {
  const [selectedBox, setSelectedBox] = useState<{pkg: string, term: number} | null>(null);
  const [isExpanded, setIsExpanded] = useState(false);
  const [isManagementMode, setIsManagementMode] = useState(false);
  const [managementCosts, setManagementCosts] = useState<ManagementCosts>(initialManagementCosts);
  const [discountRate, setDiscountRate] = useState(0.08);
  const [pricing, setPricing] = useState(() => 
    updatePricingMatrix(isManagementMode, managementCosts, {}, calculateNER)
  );

  const updatePricingData = useCallback(() => {
    const newPricing = updatePricingMatrix(isManagementMode, managementCosts, {}, calculateNER);
    setPricing(newPricing);
  }, [isManagementMode, managementCosts]);

  useEffect(() => {
    updatePricingData();
  }, [updatePricingData]);

  useEffect(() => {
    if (selectedBox) {
      setIsExpanded(true);
    }
  }, [selectedBox]);

  const handleManagementCostsChange = (newCosts: ManagementCosts) => {
    setManagementCosts(newCosts);
    updatePricingData();
  };

  const handleDiscountRateChange = (newRate: number) => {
    setDiscountRate(newRate);
    updatePricingData();
  };

  const handleModeChange = (newMode: boolean) => {
    setIsManagementMode(newMode);
    updatePricingData();
  };

  const getNER = useCallback((pkg: string, term: number, discountRate: number): number => {
    return calculateNER(pkg, term, discountRate);
  }, []);

  return (
    <div className="p-8 font-['Inter'] font-black relative min-h-screen" style={{ color: '#A82229' }}>
      <h1 className="text-2xl mb-20 text-left">Flex Office Pricing Matrix (v.01)</h1>
      <div className="flex justify-between">
        <div className="w-[800px]">
          <PricingMatrix 
            selectedBox={selectedBox} 
            setSelectedBox={setSelectedBox} 
            isManagementMode={isManagementMode}
            managementCosts={managementCosts}
            calculateNER={getNER}
            pricing={pricing}
          />
        </div>
        <div className="w-[450px] flex flex-col">
          <Sidebar 
            selectedBox={selectedBox} 
            setSelectedBox={setSelectedBox}
            isExpanded={isExpanded} 
            setIsExpanded={setIsExpanded}
            isManagementMode={isManagementMode}
            setIsManagementMode={handleModeChange}
            managementCosts={managementCosts}
            setManagementCosts={handleManagementCostsChange}
            calculateNER={getNER}
            discountRate={discountRate}
            setDiscountRate={handleDiscountRateChange}
            updatePricingData={updatePricingData}
          />
        </div>
      </div>
    </div>
  );
};

export default App;
