import { RSF, managementCosts, packages } from './data';

const calculateNER = (pkg: string, term: number, discountRate: number): number => {
  console.log(`calculateNER called with pkg: ${pkg}, term: ${term}, discountRate: ${discountRate}`);

  if (!pkg || !term || isNaN(term) || !discountRate || isNaN(discountRate)) {
    console.error('Invalid inputs to calculateNER:', { pkg, term, discountRate });
    return 0;
  }

  const monthlyRevenues: number[] = [];
  const monthlyCosts: number[] = [];

  // Calculate monthly revenue and costs for the entire term
  for (let month = 0; month < term * 12; month++) {
    let monthlyRevenue = 0;
    let monthlyCost = 0;

    // Base rent and OpEx (common to all packages)
    monthlyRevenue += (managementCosts.preOccupancy['Term Rent'] * RSF) / 12;
    monthlyCost += managementCosts.recurring['Opex (monthly)'] * RSF;

    // Package-specific calculations
    switch(pkg) {
      case 'A':
        // WiFi
        monthlyRevenue += managementCosts.recurring['WiFi'];
        monthlyCost += managementCosts.recurring['WiFi'] + (managementCosts.preOccupancy['Upfront WiFi'] * RSF) / (term * 12);

        // Branding/Signage
        monthlyRevenue += (managementCosts.preOccupancy['Branding/Signage'] * RSF) / (term * 12);
        monthlyCost += (managementCosts.preOccupancy['Branding/Signage'] * RSF) / (term * 12);
        break;

      case 'B':
        // Include everything from A, plus additional services
        monthlyRevenue += managementCosts.recurring['WiFi'];
        monthlyCost += managementCosts.recurring['WiFi'] + (managementCosts.preOccupancy['Upfront WiFi'] * RSF) / (term * 12);
        monthlyRevenue += (managementCosts.preOccupancy['Branding/Signage'] * RSF) / (term * 12);
        monthlyCost += (managementCosts.preOccupancy['Branding/Signage'] * RSF) / (term * 12);

        // Additional B services
        monthlyRevenue += (managementCosts.preOccupancy['Furniture'] * RSF) / (term * 12);
        monthlyCost += (managementCosts.preOccupancy['Furniture'] * RSF) / (term * 12);
        monthlyRevenue += managementCosts.recurring['Plants'] * RSF;
        monthlyCost += managementCosts.recurring['Plants'] * RSF;
        monthlyRevenue += (managementCosts.preOccupancy['Cabling and Drops'] * RSF) / (term * 12);
        monthlyCost += (managementCosts.preOccupancy['Cabling and Drops'] * RSF) / (term * 12);
        monthlyRevenue += managementCosts.recurring['F&B'];
        monthlyCost += managementCosts.recurring['F&B'];
        break;

      case 'C':
        // Include everything from B, plus additional services
        monthlyRevenue += managementCosts.recurring['WiFi'];
        monthlyCost += managementCosts.recurring['WiFi'] + (managementCosts.preOccupancy['Upfront WiFi'] * RSF) / (term * 12);
        monthlyRevenue += (managementCosts.preOccupancy['Branding/Signage'] * RSF) / (term * 12);
        monthlyCost += (managementCosts.preOccupancy['Branding/Signage'] * RSF) / (term * 12);
        monthlyRevenue += (managementCosts.preOccupancy['Furniture'] * RSF) / (term * 12);
        monthlyCost += (managementCosts.preOccupancy['Furniture'] * RSF) / (term * 12);
        monthlyRevenue += managementCosts.recurring['Plants'] * RSF;
        monthlyCost += managementCosts.recurring['Plants'] * RSF;
        monthlyRevenue += (managementCosts.preOccupancy['Cabling and Drops'] * RSF) / (term * 12);
        monthlyCost += (managementCosts.preOccupancy['Cabling and Drops'] * RSF) / (term * 12);
        monthlyRevenue += managementCosts.recurring['F&B'];
        monthlyCost += managementCosts.recurring['F&B'];

        // Additional C services
        monthlyRevenue += managementCosts.recurring['Dedicated WX'];
        monthlyCost += managementCosts.recurring['Dedicated WX'];
        monthlyRevenue += managementCosts.recurring['IT/AV Help Desk'] * RSF;
        monthlyCost += managementCosts.recurring['IT/AV Help Desk'] * RSF;
        break;
    }

    monthlyRevenues.push(monthlyRevenue);
    monthlyCosts.push(monthlyCost);
  }

  // Calculate monthly cash flows
  const cashFlows = monthlyRevenues.map((revenue, index) => revenue - monthlyCosts[index]);

  // Calculate NPV
  const monthlyRate = discountRate / 12;
  const npv = cashFlows.reduce((sum, cf, index) => sum + cf / Math.pow(1 + monthlyRate, index + 1), 0);

  // Calculate NER (monthly payment that would result in the same NPV)
  const numberOfPayments = term * 12;
  const ner = (npv * monthlyRate * (1 + monthlyRate) ** numberOfPayments) / ((1 + monthlyRate) ** numberOfPayments - 1);

  // Convert NER to per square foot per year
  const annualNERPerSqFt = (ner * 12) / RSF;

  console.log(`Annual NER per square foot: $${annualNERPerSqFt.toFixed(2)}`);

  return annualNERPerSqFt;
};

export default calculateNER;
