import React, { useState, useEffect } from 'react';
import { managementCosts as initialManagementCosts, RSF } from './data';
import { PricingMatrix, Sidebar } from './PricingComponents';
import calculateNER from './calculateNER';


const App: React.FC = () => {
  const [selectedBox, setSelectedBox] = useState<{pkg: string, term: number} | null>(null);
  const [isExpanded, setIsExpanded] = useState(false);
  const [isManagementMode, setIsManagementMode] = useState(false);
  const [managementCosts, setManagementCosts] = useState(initialManagementCosts);
  const [discountRate, setDiscountRate] = useState(0.08); // 8% discount rate

  const calculateTotal = (category: 'preOccupancy' | 'recurring'): number => {
    return Object.values(managementCosts[category]).reduce((total, value) => total + value, 0);
  };

  const getNER = (pkg: string, term: number): number => {
    console.log(`\n--- Calculating NER for Package ${pkg}, ${term} year term ---`);
    const ner = calculateNER(pkg, term, discountRate);
    console.log(`\nFinal NER for Package ${pkg}, ${term} year term: $${ner.toFixed(2)} per square foot per year`);
    return ner;
  };

  useEffect(() => {
    if (selectedBox) {
      setIsExpanded(true);
    }
  }, [selectedBox]);


  return (
    <div className="p-8 font-['Inter'] font-black relative min-h-screen" style={{ color: '#A82229' }}>
      <h1 className="text-2xl mb-20 text-left">Flex Office Pricing Matrix (v.01)</h1>
      <div className="flex justify-between">
        <div className="w-[800px]">
          <PricingMatrix 
            selectedBox={selectedBox} 
            setSelectedBox={setSelectedBox} 
            isManagementMode={isManagementMode}
            managementCosts={managementCosts}
            calculateNER={getNER}
            calculateTotal={calculateTotal}
          />
        </div>
        <div className="w-[450px] flex flex-col">
          <div className={`transition-all duration-500 ease-in-out ${
            isExpanded ? '-mt-[-10px]' : '-mt-[-40px]'
          } ${isExpanded && !isManagementMode ? 'pt-5' : ''}`}>
            <Sidebar 
              selectedBox={selectedBox} 
              isExpanded={isExpanded} 
              setIsExpanded={setIsExpanded}
              isManagementMode={isManagementMode}
              setIsManagementMode={setIsManagementMode}
              managementCosts={managementCosts}
              setManagementCosts={setManagementCosts}
              calculateNER={getNER}
            />
          </div>
        </div>
      </div>
    </div>
  );
};


export default App;
