// data.ts


type PackageParams = {
  [key: string]: number;
};

export type Package = {
  name: string;
  description: string;
  items: string[];
  params: {
    [key: string]: number;
  };
};

export type Packages = {
  [key: string]: Package;
};

export type PricingYear = {
  [packageKey: string]: number;
};

export type Pricing = {
  [year: string]: PricingYear;
};

export const packages: Packages = {
  A: { 
    name: 'Flexible', 
    description: 'Flexible',
    items: ['• Flexible Terms', '• Enterprise Network Connectivity', '• Day 1 Branding & Signage', '• Core Occupancy Data'],
    params: {
      'Term Rent': 126,
      'Network WiFi': 20,
      'Branding & Signage': 15,
      'Core Occupancy Data': 8,
    }
  },
  B: { 
    name: 'Inclusive', 
    description: 'Inclusive',
    items: ['• Furnished', '• Full Digital Infrastructure', '• AV Services Hardware', '• Plant Programming', '• Food & Beverage Service'],
    params: {
      'Furniture': 24,
      'Full Digital Infrastructure': 12,
      'Plants Programming': 15,
      'Food & Beverage Service': 61,
    }
  },
  C: { 
    name: 'Fully Managed', 
    description: 'Fully Managed',
    items: ['• Dedicated WX Coordinator', '• Full Mail and Package Handling', '• Hardware & Data Collection', '• IT/AV Help Desk'],
    params: {
      'Dedicated WX': 34,
      'Package Handling': 66,
      'Hardware & Data': 52,
      'IT/AV Help Desk': 22,
    }
  }
};


export const managementCosts = {
  preOccupancy: {
    'Term Rent': 80,
    'Upfront WiFi': 3.00,
    'Branding/Signage': 2.00,
    'Furniture': 27.00,
    'Plants Upfront': 0.75,
    'Cabling and Drops': 5.00,
  },
  recurring: {
    'Opex (monthly)': 1.67,
    'WiFi': 975,
    'Plants': 0.05,
    'F&B': 25,
    'Dedicated WX': 10000,
    'IT/AV Help Desk': 25,
  }
};

export const RSF: number = 5374;

export const profitData: Pricing = {
  "2": { A: 650000, B: 585000, C: 650000 },
  "3": { A: 976000, B: 878000, C: 976000 },
  "4": { A: 1400000, B: 1200000, C: 1400000 },
  "5": { A: 1600000, B: 1400000, C: 1600000 },
};

export const calculateMonthlyTotal = (pkg: string, term: number): number => {
  let total = 0;
  const monthsInTerm = term * 12;

  // Add pre-occupancy costs
  Object.values(managementCosts.preOccupancy).forEach(cost => {
    total += cost;
  });

  // Add recurring costs
  Object.values(managementCosts.recurring).forEach(cost => {
    total += cost * monthsInTerm;
  });

  // Divide by number of months to get monthly rate
  return (total * RSF) / monthsInTerm;
};

export const calculateAnnualTotal = (monthlyTotal: number): number => {
  return monthlyTotal * 12;
};

export const getAdjustedValue = (value: number, term: number): number => {
  const adjustmentFactor = 1 - (term - 2) * 0.1; // 10% decrease per year after year 2
  return value * adjustmentFactor;
};
export type ProfitData = {
  [year: string]: {
    [pkg: string]: number;
  };
};

export const formatPrice = (price: number): string => {
  if (price >= 1000000) {
    return `$${(price / 1000000).toFixed(1)}M`;
  } else {
    return `$${(price / 1000).toFixed(0)}k`;
  }
};


// Generate pricing data based on management costs
export const pricing: Pricing = {
  2: { A: calculateMonthlyTotal('A', 2), B: calculateMonthlyTotal('B', 2), C: calculateMonthlyTotal('C', 2) },
  3: { A: calculateMonthlyTotal('A', 3), B: calculateMonthlyTotal('B', 3), C: calculateMonthlyTotal('C', 3) },
  4: { A: calculateMonthlyTotal('A', 4), B: calculateMonthlyTotal('B', 4), C: calculateMonthlyTotal('C', 4) },
  5: { A: calculateMonthlyTotal('A', 5), B: calculateMonthlyTotal('B', 5), C: calculateMonthlyTotal('C', 5) }
};
