// Custom Switch component
const Switch: React.FC<{ id: string; checked: boolean; onCheckedChange: (checked: boolean) => void; className?: string }> = ({ id, checked, onCheckedChange, className }) => {
  return (
    <label htmlFor={id} className={`relative inline-block w-10 h-6 ${className}`}>
      <input
        type="checkbox"
        id={id}
        className="opacity-0 w-0 h-0"
        checked={checked}
        onChange={(e) => onCheckedChange(e.target.checked)}
      />
      <span className={`absolute cursor-pointer top-0 left-0 right-0 bottom-0 bg-gray-300 transition-all duration-300 rounded-full ${checked ? 'bg-[#A82229]' : ''}`}>
        <span className={`absolute w-5 h-5 bg-white rounded-full transition-all duration-300 ${checked ? 'left-5' : 'left-1'}`} style={{top: '2px'}}></span>
      </span>
    </label>
  );
};
const FlipCard: React.FC<{ packageKey: string; packageData: Package }> = ({ packageKey, packageData }) => {
  const [isHovered, setIsHovered] = useState(false);

  return (
    <div 
      className="box-standard relative border-4 border-[#A82229] flex items-center justify-center"
      onMouseEnter={() => setIsHovered(true)}
      onMouseLeave={() => setIsHovered(false)}
    >
      <div className={`w-full h-full transition-opacity duration-300 ${isHovered ? 'opacity-0' : 'opacity-100'} flex flex-col items-center justify-center`}>
        <h3 className="text-6xl font-bold">{packageKey}</h3>
        <p className="text-lg">{packageData.name}</p>
