import React, { useState, useRef, useEffect } from 'react';

type Package = {
  name: string;
  description: string;
  items: string[];
  params: {
    [key: string]: number;
  };
};

type Pricing = {
  [year: number]: {
    [packageKey: string]: number;
  };
};

const packages: { [key: string]: Package } = {
  A: { 
    name: 'Flexible', 
    description: 'Flexible',
    items: ['Flexible Terms', 'Enterprise Network Connectivity', 'Day 1 Branding & Signage', 'Core Occupancy Data'],
    params: {
      'Term Rent': 156,
      'Network WiFi': 20,
      'Branding and Signage': 15,
      'Base Rent': 69,
    }
  },
  B: { 
    name: 'Inclusive', 
    description: 'Inclusive',
    items: ['Furnished', 'Full Digital Infrastructure', 'AV Services Hardware', 'Plant Programming', 'Food & Beverage Service'],
    params: {
      'Furniture': 24,
      'Full Digital Infrastructure': 12,
      'Plants Programming': 15,
      'Food & Beverage Service': 61,
    }
  },
  C: { 
    name: 'Fully Managed', 
    description: 'Fully Managed',
    items: ['Dedicated WX Coordinator', 'Full Mail and Package Handling', 'Hardware & Data Collection', 'IT/AV Help Desk'],
    params: {
      'Dedicated WX': 34,
      'Package Handling': 66,
      'Hardware & Data': 52,
      'IT/AV Help Desk': 22,
    }
  }
};

const pricing: Pricing = {
  2: { A: 100000, B: 120000, C: 140000 },
  3: { A: 80000, B: 100000, C: 120000 },
  4: { A: 60000, B: 80000, C: 100000 },
  5: { A: 60000, B: 80000, C: 100000 }
};




const FlipCard: React.FC<{ packageKey: string; packageData: Package }> = ({ packageKey, packageData }) => {
  const [isHovered, setIsHovered] = useState(false);



  return (
    <div 
      className="border-4 border-[#A82229] p-2 text-center aspect-square relative"
      onMouseEnter={() => setIsHovered(true)}
      onMouseLeave={() => setIsHovered(false)}
    >
      <div className={`w-full h-full transition-opacity duration-300 ${isHovered ? 'opacity-0' : 'opacity-100'}`}>
        <h3 className="text-6xl font-bold">{packageKey}</h3>
        <p className="text-lg">{packageData.name}</p>
      </div>
      <div className={`absolute top-0 left-0 w-full h-full bg-white p-2 overflow-auto transition-opacity duration-300 ${isHovered ? 'opacity-100' : 'opacity-0'}`}>
        <ul className="text-left text-sm">
          {packageData.items.map((item, index) => (
            <li key={index} className="mb-1">{item}</li>
          ))}
        </ul>
      </div>
    </div>
  );
};

const App: React.FC = () => {
  const [selectedPackage, setSelectedPackage] = useState<string | null>(null);
  const [selectedTerm, setSelectedTerm] = useState<number | null>(null);

  const handleSelection = (pkg: string, term: number) => {
    setSelectedPackage(pkg);
    setSelectedTerm(term);
  };

  const getOpacity = (term: number) => {
    switch(term) {
      case 2: return 'opacity-50';
      case 3: return 'opacity-80';
      case 4: return 'opacity-100';
      case 5: return 'opacity-15';
      default: return 'opacity-100';
    }
  };

  // Comment: Adjust the 'w-28 h-28' classes to change the size of each box
  const boxClass = "w-28 h-28 flex items-center justify-center text-center";

  return (
    <div className="flex p-4 font-['Inter'] font-black" style={{ color: '#A82229' }}>
      <div>
        <div className="grid grid-cols-4 gap-1 mb-1">
          <div className={`${boxClass} invisible`}></div>
          {Object.entries(packages).map(([key, value]) => (
            <FlipCard key={key} packageKey={key} packageData={value} />
          ))}
        </div>
        <div className="grid grid-cols-4 gap-1">
          {Object.entries(pricing).map(([term, prices]) => (
            <React.Fragment key={term}>
              <div className={`${boxClass} border-4 border-[#A82229] font-bold text-2xl ${parseInt(term) === 5 ? 'opacity-15' : ''}`}>
                Year {term.padStart(2, '0')}
              </div>
              {Object.entries(prices).map(([pkg, price]) => (
                <button
                  key={`${term}-${pkg}`}
                  className={`${boxClass} bg-[#A82229] text-white text-2xl font-bold
                    ${getOpacity(parseInt(term))}
                    ${selectedPackage === pkg && selectedTerm === parseInt(term) ? 'ring-4 ring-yellow-400' : ''}
                    transition-all duration-300`}
                  onClick={() => handleSelection(pkg, parseInt(term))}
                >
                  <div>
                    <span className="text-lg">$</span>
                    <span className="text-3xl">{price / 1000}</span>
                    <span className="text-lg">k</span>
                  </div>
                </button>
              ))}
            </React.Fragment>
          ))}
        </div>
      </div>
      <div className="pl-4 self-start" style={{marginTop: '7rem'}}>
        <Sidebar selectedPackage={selectedPackage} selectedTerm={selectedTerm} />
      </div>
    </div>
  );
};

interface SidebarProps {
  selectedPackage: string | null;
  selectedTerm: number | null;
}

const Sidebar: React.FC<SidebarProps> = ({ selectedPackage, selectedTerm }) => {
  if (!selectedPackage || !selectedTerm) {
    return (
      <div className="border-6 border-[#A82229] rounded-lg p-4 text-[#A82229]">
        <div className="mb-4">160 Varick in Hudson</div>
        <div className="flex justify-between mb-4">
          <div className="font-bold">RSF:</div>
          <div className="text-2xl">5,374</div>
        </div>
      </div>
    );
  }




  const price = pricing[selectedTerm][selectedPackage];

  const allParams = {
    ...packages.A.params,
    ...packages.B.params,
    ...packages.C.params
  };

  return (
    <div className="border border-[#A82229] rounded-lg p-4 text-[#A82229] animate-fadeIn">
      <div className="flex justify-between items-center mb-4">
        <div>
          <div className="text-3xl font-bold">{packages[selectedPackage].name}</div>
        </div>
        <div className="flex items-center">
          <div className="bg-[#A82229] text-white rounded-full w-8 h-8 flex items-center justify-center mr-2">
            {selectedPackage}
          </div>
          <button className="bg-white text-[#A82229] border border-[#A82229] px-4 py-1 rounded">Select</button>
        </div>
      </div>


{/*  */}



      <div className="flex justify-between mb-4">
        <div>160 Varick in Hudson</div>
        <div>{selectedTerm} yr</div>
      </div>
      <div className="flex justify-between mb-4">
        <div className="font-bold">RSF:</div>
        <div className="text-2xl">5,374</div>
      </div>
      {Object.entries(allParams).map(([key, value], index, array) => (
        <React.Fragment key={key}>
          <div className={`flex justify-between mb-4 ${
            (selectedPackage === 'A' && packages.A.params[key] === undefined) ||
            (selectedPackage === 'B' && packages.A.params[key] === undefined && packages.B.params[key] === undefined)
              ? 'opacity-20'
              : ''
          }`}>
            <div>{key}</div>
            <div>${value}</div>
          </div>
          {(index === Object.keys(packages.A.params).length - 1 || 
            index === Object.keys(packages.A.params).length + Object.keys(packages.B.params).length - 1) && 
            <hr className="border-t border-[#A82229] my-4" />}
        </React.Fragment>
      ))}
      <div className="mt-4 pt-4 border-t border-[#A82229]">
        <div className="flex justify-between font-bold">
          <div>TOTAL PER SF</div>
          <div>$234</div>
        </div>
        <div className="flex justify-between font-bold text-lg bg-[#A82229] text-white p-2 mt-2">
          <div>TOTAL MONTHLY</div>
          <div>${(price / 12).toLocaleString()}</div>
        </div>
        <div className="flex justify-between font-bold text-lg bg-[#A82229] text-white p-2 mt-2">
          <div>TOTAL ANNUAL</div>
          <div>${price.toLocaleString()}</div>
        </div>
      </div>
    </div>
  );
};

export default App;
