import React, { useState, useEffect, useCallback, useRef } from 'react';

type Package = {
  name: string;
  description: string;
  items: string[];
  params: {
    [key: string]: number;
  };
};

type Pricing = {
  [year: number]: {
    [packageKey: string]: number;
  };
};



const packages: { [key: string]: Package } = {
  A: { 
    name: 'Flexible', 
    description: 'Flexible',
    items: ['• Flexible Terms', '• Enterprise Network Connectivity', '• Day 1 Branding & Signage', '• Core Occupancy Data'],
    params: {
      'Term Rent': 156,
      'Network WiFi': 20,
      'Branding and Signage': 15,
      'Base Rent': 69,
    }
  },
  B: { 
    name: 'Inclusive', 
    description: 'Inclusive',
    items: ['• Furnished', '• Full Digital Infrastructure', '• AV Services Hardware', '• Plant Programming', '• Food & Beverage Service'],
    params: {
      'Furniture': 24,
      'Full Digital Infrastructure': 12,
      'Plants Programming': 15,
      'Food & Beverage Service': 61,
    }
  },
  C: { 
    name: 'Fully Managed', 
    description: 'Fully Managed',
    items: ['• Dedicated WX Coordinator', '• Full Mail and Package Handling', '• Hardware & Data Collection', '• IT/AV Help Desk'],
    params: {
      'Dedicated WX': 34,
      'Package Handling': 66,
      'Hardware & Data': 52,
      'IT/AV Help Desk': 22,
    }
  }
};

const pricing: Pricing = {
  2: { A: 100000, B: 120000, C: 140000 },
  3: { A: 80000, B: 100000, C: 120000 },
  4: { A: 60000, B: 80000, C: 100000 },
  5: { A: 60000, B: 80000, C: 100000 }
};



const FlipCard: React.FC<{ packageKey: string; packageData: Package }> = ({ packageKey, packageData }) => {

  const [isHovered, setIsHovered] = useState(false);



  return (
    <div 
      className="box-standard relative border-4 border-[#A82229] flex items-center justify-center"
      onMouseEnter={() => setIsHovered(true)}
      onMouseLeave={() => setIsHovered(false)}
    >
      <div className={`w-full h-full transition-opacity duration-300 ${isHovered ? 'opacity-0' : 'opacity-100'} flex flex-col items-center justify-center`}>
        <h3 className="text-6xl font-bold">{packageKey}</h3>
        <p className="text-lg">{packageData.name}</p>
      </div>
      <div className={`absolute top-0 left-0 w-full h-full bg-white p-2 overflow-auto transition-opacity duration-300 ${isHovered ? 'opacity-100' : 'opacity-0'}`}>
        <ul className="text-left text-sm leading-tight">
          {packageData.items.map((item, index) => (
            <li key={index} className="mb-1">{item}</li>
          ))}
        </ul>
      </div>
    </div>
  );
};


const App: React.FC = () => {
  const [selectedBox, setSelectedBox] = useState<{pkg: string, term: number} | null>(null);
  const [columnSpacing, setColumnSpacing] = useState(0);
  const [rowSpacing, setRowSpacing] = useState(20);
  const [matrixWidthPercentage, setMatrixWidthPercentage] = useState(75); // Default 75% of container
  const [containerWidth, setContainerWidth] = useState(1200); // Default container width


  const [isDragging, setIsDragging] = useState(false);
  const containerRef = useRef<HTMLDivElement>(null);


  const handleSelection = (pkg: string, term: number) => {
    setSelectedBox({pkg, term});
  };

  const isSelected = (pkg: string, term: number) => {
    return selectedBox?.pkg === pkg && selectedBox.term === term;
  };

  const getOpacity = (term: number) => {
    switch(term) {
      case 2: return 'opacity-50';
      case 3: return 'opacity-80';
      case 4: return 'opacity-100';
      case 5: return 'opacity-15';
      default: return 'opacity-100';
    }
  };

  // Calculate column gap based on slider value
  const calculateColumnGap = (value: number) => {
    return value * 0.4 - 20;
  };

  const handleMouseDown = useCallback((e: React.MouseEvent) => {
    e.preventDefault();
    setIsDragging(true);
  }, []);

  const handleMouseUp = useCallback(() => {
    setIsDragging(false);
  }, []);

  const handleMouseMove = useCallback((e: React.MouseEvent) => {
    if (isDragging && containerRef.current) {
      const containerRect = containerRef.current.getBoundingClientRect();
      const newMatrixWidth = e.clientX - containerRect.left;
      const newPercentage = (newMatrixWidth / containerRect.width) * 100;
      setMatrixWidthPercentage(Math.max(50, Math.min(newPercentage, 90))); // Limit between 50% and 90%
    }
  }, [isDragging]);

  useEffect(() => {
    const handleResize = () => {
      if (containerRef.current) {
        setContainerWidth(containerRef.current.offsetWidth);
      }
    };

    handleResize(); // Initial call
    window.addEventListener('resize', handleResize);

    return () => {
      window.removeEventListener('resize', handleResize);
    };
  }, []);



  return (
    <div className="p-4 font-['Inter'] font-black" style={{ color: '#A82229' }}>
      <h1 className="text-3xl mb-8 text-left">Real Time Pricing Matrix</h1>
      {/* ... (controls remain unchanged) */}
      <div 
        className="flex relative" 
        ref={containerRef}
        onMouseMove={handleMouseMove}
      >
<div style={{ width: `${100 - matrixWidthPercentage}%`, minWidth: '200px' }}>
<Sidebar selectedBox={selectedBox} />
          <div className="grid grid-cols-4 gap-x-[var(--column-gap)] mb-[var(--row-gap)]">
            <div className="invisible"></div>
            {Object.entries(packages).map(([key, value]) => (
              <FlipCard key={key} packageKey={key} packageData={value} />
            ))}
          </div>
          <div className="grid grid-cols-4 gap-x-[var(--column-gap)] gap-y-[var(--row-gap)]">

            {Object.entries(pricing).map(([term, prices]) => (
              <React.Fragment key={term}>
                <div className={`box-standard flex items-center justify-center text-center border-4 border-[#A82229] font-bold text-2xl ${term === '5' ? 'opacity-10' : ''}`}>
                  Year {term.padStart(2, '0')}
                </div>
                {Object.entries(prices).map(([pkg, price]) => (
                  <button
                    key={`${term}-${pkg}`}
                    className={`box-standard flex items-center justify-center text-center
                      bg-[#A82229] text-white text-2xl font-bold
                      ${isSelected(pkg, parseInt(term)) ? 'bg-white !text-[#A82229] border-8 border-[#A82229]' : `border-4 border-[#A82229] ${getOpacity(parseInt(term))}`}
                      transition-all duration-300`}
                    onClick={() => handleSelection(pkg, parseInt(term))}
                  >
                    <div className={`${isSelected(pkg, parseInt(term)) ? 'transform scale-[1.8]' : ''}`}>
                      <span className="text-lg">$</span>
                      <span className="text-3xl">{price / 1000}</span>
                      <span className="text-lg">k</span>
                    </div>
                  </button>
                ))}
              </React.Fragment>
            ))}
          </div>
          </div>
        <div 
 className="absolute top-0 bottom-0 cursor-col-resize"
 style={{
   left: `calc(100% - ${sidebarWidth}px - 4px)`,
   width: '8px',
 }}
 onMouseDown={handleMouseDown}
></div>
<div style={{ width: `${sidebarWidth}px`, minWidth: '200px' }}>
 <Sidebar selectedBox={selectedBox} />
</div>
</div>

{/* Sliders positioned at the bottom-right */}
<div className="absolute bottom-4 right-4 flex flex-col items-end">
<div className="mb-2">
 <label htmlFor="columnSpacing" className="mr-2">Column:</label>
 <input
  id="matrixWidth"
  type="range"
  min="50"
  max="90"
  value={matrixWidthPercentage}
  onChange={handleMatrixWidthChange}
  className="w-32 slider-red"
/>
<span className="ml-2">{matrixWidthPercentage.toFixed(0)}%</span>
</div>
<div>
 <label htmlFor="rowSpacing" className="mr-2">Row:</label>
 <input
   id="rowSpacing"
   type="range"
   min="0"
   max="100"
   value={rowSpacing}
   onChange={(e) => setRowSpacing(Number(e.target.value))}
   className="w-32 slider-red"
 />
</div>
</div>

{/* Custom styles for red sliders */}
<style jsx>{`
.slider-red {
 -webkit-appearance: none;
 width: 100%;
 height: 15px;
 border-radius: 5px;  
 background: #d3d3d3;
 outline: none;
 opacity: 0.7;
 -webkit-transition: .2s;
 transition: opacity .2s;
}

.slider-red::-webkit-slider-thumb {
 -webkit-appearance: none;
 appearance: none;
 width: 25px;
 height: 25px;
 border-radius: 50%; 
 background: #A82229;
 cursor: pointer;
}

.slider-red::-moz-range-thumb {
 width: 25px;
 height: 25px;
 border-radius: 50%;
 background: #A82229;
 cursor: pointer;
}
`}</style>
</div>
);
};





interface SidebarProps {
  selectedBox: {pkg: string, term: number} | null;
}



const Sidebar: React.FC<SidebarProps> = ({ selectedBox }) => {
  const getAdjustedValue = (value: number, term: number) => {
    const adjustmentFactor = 1 - (term - 2) * 0.1; // 10% decrease per year after year 2
    return Math.round(value * adjustmentFactor);
  };

  if (!selectedBox) {
    return (
      <div className="border-4 border-[#A82229] rounded-3xl p-6 text-[#A82229]">
        <div className="mb-4 text-xl">160 Varick in Hudson</div>
        <div className="flex justify-between mb-4">
          <div>0 yr</div>
        </div>
        <div className="flex justify-between mb-4 bg-[#A82229] text-white p-2 rounded-lg">
          <div className="font-bold">RSF:</div>
          <div className="text-2xl">5,374</div>
        </div>
      </div>
    );
  }

  const {pkg, term} = selectedBox;
  const price = pricing[term][pkg];

  const allParams = {
    ...packages.A.params,
    ...packages.B.params,
    ...packages.C.params
  };

  const getOpacity = (packageKey: string) => {
    switch(pkg) {
      case 'A': return packageKey === 'A' ? 'opacity-100' : 'opacity-20';
      case 'B': return packageKey === 'C' ? 'opacity-20' : 'opacity-100';
      case 'C': return 'opacity-100';
      default: return 'opacity-100';
    }
  };

  return (
    <div className="border-4 border-[#A82229] rounded-3xl p-6 text-[#A82229] animate-fadeIn">
      <div className="flex justify-between items-center mb-4">
        <div className="text-2xl font-bold">Tenant</div>
        <div className="flex items-center">
          <div className="bg-[#A82229] text-white rounded-full w-8 h-8 flex items-center justify-center mr-2 text-xl">
            {pkg}
          </div>
          <button className="bg-white text-[#A82229] border-2 border-[#A82229] px-4 py-1 rounded-full text-sm">Select</button>
        </div>
      </div>
      <div className="text-xl mb-2">Package {pkg}</div>
      <div className="flex justify-between mb-4 text-lg">
        <div>160 Varick in Hudson</div>
        <div>{term} yr</div>
      </div>
      <div className="flex justify-between mb-6 bg-[#A82229] text-white p-2 rounded-lg">
        <div className="font-bold text-lg">RSF:</div>
        <div className="text-2xl">5,374</div>
      </div>
      {['A', 'B', 'C'].map((packageKey) => (
        <div key={packageKey} className={`mb-4 ${getOpacity(packageKey)}`}>
          {Object.entries(packages[packageKey as keyof typeof packages].params).map(([key, value]) => (
            <div key={key} className="flex justify-between mb-2">
              <div>{key}</div>
              <div>${getAdjustedValue(value, term)}</div>
            </div>
          ))}
          {packageKey !== 'C' && <hr className="border-t border-[#A82229] my-4" />}
        </div>
      ))}
      <div className="mt-6 pt-4 border-t border-[#A82229]">
        <div className="flex justify-between font-bold text-lg">
          <div>TOTAL PER SF</div>
          <div>$234</div>
        </div>
        <div className="flex justify-between font-bold text-lg bg-[#A82229] text-white p-2 mt-2 rounded-lg">
          <div>TOTAL MONTHLY</div>
          <div>${(price / 12).toLocaleString()}</div>
        </div>
        <div className="flex justify-between font-bold text-lg bg-[#A82229] text-white p-2 mt-2 rounded-lg">
          <div>TOTAL ANNUAL</div>
          <div>${price.toLocaleString()}</div>
        </div>
      </div>
    </div>
  );
};

export default App;
