import React, { useState } from 'react';

type Package = {
  name: string;
  description: string;
  items: string[];
  params: {
    [key: string]: number;
  };
};

type Pricing = {
  [year: number]: {
    [packageKey: string]: number;
  };
};



const packages: { [key: string]: Package } = {
  A: { 
    name: 'Flexible', 
    description: 'Flexible',
    items: ['• Flexible Terms', '• Enterprise Network Connectivity', '• Day 1 Branding & Signage', '• Core Occupancy Data'],
    params: {
      'Term Rent': 156,
      'Network WiFi': 20,
      'Branding and Signage': 15,
      'Base Rent': 69,
    }
  },
  B: { 
    name: 'Inclusive', 
    description: 'Inclusive',
    items: ['• Furnished', '• Full Digital Infrastructure', '• AV Services Hardware', '• Plant Programming', '• Food & Beverage Service'],
    params: {
      'Furniture': 24,
      'Full Digital Infrastructure': 12,
      'Plants Programming': 15,
      'Food & Beverage Service': 61,
    }
  },
  C: { 
    name: 'Fully Managed', 
    description: 'Fully Managed',
    items: ['• Dedicated WX Coordinator', '• Full Mail and Package Handling', '• Hardware & Data Collection', '• IT/AV Help Desk'],
    params: {
      'Dedicated WX': 34,
      'Package Handling': 66,
      'Hardware & Data': 52,
      'IT/AV Help Desk': 22,
    }
  }
};

const FlipCard: React.FC<{ packageKey: string; packageData: Package }> = ({ packageKey, packageData }) => {
  const [isHovered, setIsHovered] = useState(false);

  return (
    <div 
      className="box-standard relative border-4 border-[#A82229] flex items-center justify-center"
      onMouseEnter={() => setIsHovered(true)}
      onMouseLeave={() => setIsHovered(false)}
    >
      <div className={`w-full h-full transition-opacity duration-300 ${isHovered ? 'opacity-0' : 'opacity-100'} flex flex-col items-center justify-center`}>
        <h3 className="text-6xl font-bold">{packageKey}</h3>
        <p className="text-lg">{packageData.name}</p>
      </div>
      <div className={`absolute top-0 left-0 w-full h-full bg-white p-2 overflow-auto transition-opacity duration-300 ${isHovered ? 'opacity-100' : 'opacity-0'}`}>
        <ul className="text-left text-sm leading-tight">
          {packageData.items.map((item, index) => (
            <li key={index} className="mb-1">{item}</li>
          ))}
        </ul>
      </div>
    </div>
  );
};



interface SidebarProps {
  selectedBox: {pkg: string, term: number} | null;
}

const Sidebar: React.FC<SidebarProps> = ({ selectedBox }) => {
  const getAdjustedValue = (value: number, term: number) => {
    const adjustmentFactor = 1 - (term - 2) * 0.1; // 10% decrease per year after year 2
    return Math.round(value * adjustmentFactor);
  };

  if (!selectedBox) {
    return (
      <div className="border-4 border-[#A82229] rounded-lg p-4 text-[#A82229]">
        <div className="mb-4">160 Varick in Hudson</div>
        <div className="flex justify-between mb-4">
          <div>0 yr</div>
        </div>
        <div className="flex justify-between mb-4 bg-[#A82229] text-white p-2">
          <div className="font-bold">RSF:</div>
          <div className="text-2xl">5,374</div>
        </div>
      </div>
    );
  }

  const {pkg, term} = selectedBox;
  const price = pricing[term][pkg];

  const allParams = {
    ...packages.A.params,
    ...packages.B.params,
    ...packages.C.params
  };

  return (
    <div className="border-4 border-[#A82229] rounded-lg p-4 text-[#A82229] animate-fadeIn">
      <div className="flex justify-between items-center mb-4">
        <div>
          <div className="text-3xl font-bold">{packages[pkg].name}</div>
        </div>
        <div className="flex items-center">
          <div className="bg-[#A82229] text-white rounded-full w-8 h-8 flex items-center justify-center mr-2">
            {pkg}
          </div>
          <button className="bg-white text-[#A82229] border border-[#A82229] px-4 py-1 rounded">Select</button>
        </div>
      </div>
      <div className="flex justify-between mb-4">
        <div>160 Varick in Hudson</div>
        <div>{term} yr</div>
      </div>
      <div className="flex justify-between mb-4 bg-[#A82229] text-white p-2">
        <div className="font-bold">RSF:</div>
        <div className="text-2xl">5,374</div>
      </div>
      {Object.entries(allParams).map(([key, value], index, array) => (
        <React.Fragment key={key}>
          <div className={`flex justify-between mb-4 ${
            (pkg === 'A' && packages.A.params[key] === undefined) ||
            (pkg === 'B' && packages.A.params[key] === undefined && packages.B.params[key] === undefined)
              ? 'opacity-20'
              : ''
          }`}>
            <div>{key}</div>
            <div>${getAdjustedValue(value, term)}</div>
          </div>
          {(index === Object.keys(packages.A.params).length - 1 || 
            index === Object.keys(packages.A.params).length + Object.keys(packages.B.params).length - 1) && 
            <hr className="border-t border-[#A82229] my-4" />}
        </React.Fragment>
      ))}
      <div className="mt-4 pt-4 border-t border-[#A82229]">
        <div className="flex justify-between font-bold">
          <div>TOTAL PER SF</div>
          <div>$234</div>
        </div>
        <div className="flex justify-between font-bold text-lg bg-[#A82229] text-white p-2 mt-2">
          <div>TOTAL MONTHLY</div>
          <div>${(price / 12).toLocaleString()}</div>
        </div>
        <div className="flex justify-between font-bold text-lg bg-[#A82229] text-white p-2 mt-2">
          <div>TOTAL ANNUAL</div>
          <div>${price.toLocaleString()}</div>
        </div>
      </div>
    </div>
  );
};

export default App;
