// data.ts

// Type definitions
type PackageParams = {
  [key: string]: number;
};

type Package = {
  name: string;
  description: string;
  items: string[];
  params: PackageParams;
};

type Packages = {
  [key: string]: Package;
};

type PricingYear = {
  [packageKey: string]: number;
};

type Pricing = {
  [year: number]: PricingYear;
};

// Data
export const packages: Packages = {
  A: { 
    name: 'Flexible', 
    description: 'Flexible',
    items: ['• Flexible Terms', '• Enterprise Network Connectivity', '• Day 1 Branding & Signage', '• Core Occupancy Data'],
    params: {
      'Term Rent': 126,
      'Network WiFi': 20,
      'Branding & Signage': 15,
      'Core Occupancy Data': 8,
    }
  },
  B: { 
    name: 'Inclusive', 
    description: 'Inclusive',
    items: ['• Furnished', '• Full Digital Infrastructure', '• AV Services Hardware', '• Plant Programming', '• Food & Beverage Service'],
    params: {
      'Furniture': 24,
      'Full Digital Infrastructure': 12,
      'Plants Programming': 15,
      'Food & Beverage Service': 61,
    }
  },
  C: { 
    name: 'Fully Managed', 
    description: 'Fully Managed',
    items: ['• Dedicated WX Coordinator', '• Full Mail and Package Handling', '• Hardware & Data Collection', '• IT/AV Help Desk'],
    params: {
      'Dedicated WX': 34,
      'Package Handling': 66,
      'Hardware & Data': 52,
      'IT/AV Help Desk': 22,
    }
  }
};

export const pricing: Pricing = {
  2: { A: 100000, B: 120000, C: 140000 },
  3: { A: 80000, B: 100000, C: 120000 },
  4: { A: 60000, B: 80000, C: 100000 },
  5: { A: 60000, B: 80000, C: 100000 }
};

export const RSF: number = 5374; // Rentable Square Feet

// Utility functions
export const calculateMonthlyTotal = (pkg: string, term: number): number => {
  let total = 0;
  for (let i = 0; i <= pkg.charCodeAt(0) - 65; i++) {
    const packageKey = String.fromCharCode(65 + i) as keyof typeof packages;
    Object.values(packages[packageKey].params).forEach(value => {
      total += getAdjustedValue(value, term);
    });
  }
  return total * RSF / 12;
};

export const calculateAnnualTotal = (monthlyTotal: number): number => {
  return monthlyTotal * 12;
};

export const getAdjustedValue = (value: number, term: number): number => {
  const adjustmentFactor = 1 - (term - 2) * 0.1; // 10% decrease per year after year 2
  return value * adjustmentFactor;
};

export const formatPrice = (price: number): string => {
  const roundedPrice = Math.round(price / 1000);
  return `$${roundedPrice}k`;
};
