import React, { useState } from 'react';

// ... [previous type definitions and data remain the same]

const FlipCard: React.FC<{ packageKey: string; packageData: Package }> = ({ packageKey, packageData }) => {
  const [isFlipped, setIsFlipped] = useState(false);

  return (
    <div 
      className="border-4 border-[#A82229] p-2 text-center aspect-square relative"
      onMouseEnter={() => setIsFlipped(true)}
      onMouseLeave={() => setIsFlipped(false)}
    >
      <div className={`w-full h-full transition-transform duration-500 ${isFlipped ? 'rotate-y-180' : ''}`}>
        <div className="absolute w-full h-full backface-hidden">
          <h3 className="text-6xl font-bold">{packageKey}</h3>
          <p className="text-lg">{packageData.description}</p>
        </div>
        <div className="absolute w-full h-full backface-hidden rotate-y-180 bg-white p-2 overflow-auto">
          <ul className="text-left">
            {packageData.items.map((item, index) => (
              <li key={index} className="mb-2">{item}</li>
            ))}
          </ul>
        </div>
      </div>
    </div>
  );
};

const App: React.FC = () => {
  const [selectedPackage, setSelectedPackage] = useState<string | null>(null);
  const [selectedTerm, setSelectedTerm] = useState<number | null>(null);

  const handleSelection = (pkg: string, term: number) => {
    setSelectedPackage(pkg);
    setSelectedTerm(term);
  };

  const getOpacity = (term: number, isSelected: boolean) => {
    if (selectedPackage && selectedTerm) {
      return isSelected ? '100' : '30';
    }
    switch(term) {
      case 2: return '50';
      case 3: return '80';
      case 4: return '100';
      case 5: return '20';
      default: return '100';
    }
  };

  return (
    <div className="flex p-4 font-['Inter'] font-black" style={{ color: '#A82229' }}>
      <div className="w-2/3">
        <div className="grid grid-cols-4 gap-1 mb-1">
          <div className="col-span-1"></div>
          {Object.entries(packages).map(([key, value]) => (
            <FlipCard key={key} packageKey={key} packageData={value} />
          ))}
        </div>
        <div className="grid grid-cols-4 gap-1 max-w-3xl">
          <div className="font-bold self-end">Term</div>
          {Object.keys(packages).map((key) => (
            <div key={key} className="font-bold text-center"></div>
          ))}
          {Object.entries(pricing).map(([term, prices]) => (
            <React.Fragment key={term}>
              <div className={`font-bold text-2xl self-center border-4 border-[#A82229] p-2 ${parseInt(term) === 5 ? 'opacity-20' : ''}`}>
                Year {term.padStart(2, '0')}
              </div>
              {Object.entries(prices).map(([pkg, price]) => (
                <button
                  key={`${term}-${pkg}`}
                  className={`p-2 bg-[#A82229] text-white text-2xl font-bold aspect-square flex flex-col justify-end items-center
                    opacity-${getOpacity(parseInt(term), selectedPackage === pkg && selectedTerm === parseInt(term))}`}
                  onClick={() => handleSelection(pkg, parseInt(term))}
                >
                  <div className="flex items-baseline">
                    <span className="text-sm">$</span>
                    <span className="text-2xl">{price / 1000}</span>
                    <span className="text-sm">k</span>
                  </div>
                </button>
              ))}
            </React.Fragment>
          ))}
        </div>
      </div>
      <div className="w-1/3 pl-4 self-start" style={{marginTop: '5rem'}}>
        <Sidebar selectedPackage={selectedPackage} selectedTerm={selectedTerm} />
      </div>
    </div>
  );
};

interface SidebarProps {
  selectedPackage: string | null;
  selectedTerm: number | null;
}

const Sidebar: React.FC<SidebarProps> = ({ selectedPackage, selectedTerm }) => {
  if (!selectedPackage || !selectedTerm) {
    return (
      <div className="border border-[#A82229] rounded-lg p-4 text-[#A82229]">
        <div className="mb-4">160 Varick in Hudson</div>
        <div className="flex justify-between mb-4">
          <div className="font-bold">RSF:</div>
          <div className="text-2xl">5,374</div>
        </div>
      </div>
    );
  }

  const price = pricing[selectedTerm][selectedPackage];

  const allParams = {
    ...packages.A.params,
    ...packages.B.params,
    ...packages.C.params
  };

  return (
    <div className="border border-[#A82229] rounded-lg p-4 text-[#A82229] animate-fadeIn">
      <div className="flex justify-between items-center mb-4">
        <div>
          <div className="text-3xl font-bold">{packages[selectedPackage].name}</div>
        </div>
        <div className="flex items-center">
          <div className="bg-[#A82229] text-white rounded-full w-8 h-8 flex items-center justify-center mr-2">
            {selectedPackage}
          </div>
          <button className="bg-white text-[#A82229] border border-[#A82229] px-4 py-1 rounded">Select</button>
        </div>
      </div>
      <div className="flex justify-between mb-4">
        <div>160 Varick in Hudson</div>
        <div>{selectedTerm} yr</div>
      </div>
      <div className="flex justify-between mb-4">
        <div className="font-bold">RSF:</div>
        <div className="text-2xl">5,374</div>
      </div>
      {Object.entries(allParams).map(([key, value], index, array) => (
        <React.Fragment key={key}>
          <div className={`flex justify-between mb-4 ${
            (selectedPackage === 'A' && packages.A.params[key] === undefined) ||
            (selectedPackage === 'B' && packages.A.params[key] === undefined && packages.B.params[key] === undefined)
              ? 'opacity-20'
              : ''
          }`}>
            <div>{key}</div>
            <div>${value}</div>
          </div>
          {(index === Object.keys(packages.A.params).length - 1 || 
            index === Object.keys(packages.A.params).length + Object.keys(packages.B.params).length - 1) && 
            <hr className="border-t border-[#A82229] my-4" />}
        </React.Fragment>
      ))}
      <div className="mt-4 pt-4 border-t border-[#A82229]">
        <div className="flex justify-between font-bold">
          <div>TOTAL PER SF</div>
          <div>$234</div>
        </div>
        <div className="flex justify-between font-bold text-lg bg-[#A82229] text-white p-2 mt-2">
          <div>TOTAL MONTHLY</div>
          <div>${(price / 12).toLocaleString()}</div>
        </div>
        <div className="flex justify-between font-bold text-lg bg-[#A82229] text-white p-2 mt-2">
          <div>TOTAL ANNUAL</div>
          <div>${price.toLocaleString()}</div>
        </div>
      </div>
    </div>
  );
};

export default App;
