// data.ts

import calculateNER from './calculateNER';

export type PackageParams = {
  [key: string]: number;
};

export type Package = {
  name: string;
  description: string;
  items: string[];
  params: PackageParams;
};

export type Packages = {
  [key: string]: Package;
};

export type PricingYear = {
  [packageKey: string]: number;
};

export type Pricing = {
  [year: string]: PricingYear;
};

export const packages: Packages = {
  A: { 
    name: 'Flexible', 
    description: 'Flexible',
    items: ['• Flexible Terms', '• Enterprise Network Connectivity', '• Day 1 Branding & Signage', '• Core Occupancy Data'],
    params: {
      'Term Rent': 126,
      'Network WiFi': 20,
      'Branding & Signage': 15,
      'Core Occupancy Data': 8,
    }
  },
  B: { 
    name: 'Inclusive', 
    description: 'Inclusive',
    items: ['• Furnished', '• Full Digital Infrastructure', '• AV Services Hardware', '• Plant Programming', '• Food & Beverage Service'],
    params: {
      'Furniture': 24,
      'Full Digital Infrastructure': 12,
      'Plants Programming': 15,
      'Food & Beverage Service': 61,
    }
  },
  C: { 
    name: 'Fully Managed', 
    description: 'Fully Managed',
    items: ['• Dedicated WX Coordinator', '• Full Mail and Package Handling', '• Hardware & Data Collection', '• IT/AV Help Desk'],
    params: {
      'Dedicated WX': 34,
      'Package Handling': 66,
      'Hardware & Data': 52,
      'IT/AV Help Desk': 22,
    }
  }
};
export type ManagementCosts = {
  preOccupancy: {
    [key: string]: number;
  };
  recurring: {
    [key: string]: number;
  };
};


export const managementCosts: ManagementCosts = {
  preOccupancy: {
    'Term Rent': 80,
    'Upfront WiFi': 3.00,
    'Branding/Signage': 2.00,
    'Furniture': 27.00,
    'Plants Upfront': 0.75,
    'Cabling and Drops': 5.00,
  },
  recurring: {
    'Opex (monthly)': 1.67,
    'WiFi': 975,
    'Plants': 0.05,
    'F&B': 25,
    'Dedicated WX': 10000,
    'IT/AV Help Desk': 25,
  }
};

export const RSF: number = 5374;

export const calculateTermRent = (term: number) => {
  const baseTermRent = 80; // Base Term Rent for 5-year term
  const termPremium = 0.1; // 10% default term premium
  if (term === 5) return baseTermRent;
  return baseTermRent * (1 + ((5 - term) * termPremium));
};

export const calculateMonthlyTotal = (pkg: string, term: number): number => {
  let monthlyTotal = 0;
  const monthsInTerm = term * 12;

  // Base Rent
  const baseRent = calculateTermRent(term);
  monthlyTotal += (baseRent + managementCosts.recurring['Opex (monthly)']) * RSF / 12;

  // Enterprise grade WiFi
  monthlyTotal += managementCosts.recurring['WiFi'];
  monthlyTotal += (managementCosts.preOccupancy['Upfront WiFi'] * RSF) / monthsInTerm;

  if (pkg === 'B' || pkg === 'C') {
    // Furnished
    monthlyTotal += (managementCosts.preOccupancy['Furniture'] * RSF) / monthsInTerm;

    // Full Digital Infrastructure
    monthlyTotal += 975;

    // Food & Beverage Service
    monthlyTotal += 1343.50;
  }

  if (pkg === 'C') {
    // Dedicated Coordinator
    monthlyTotal += 15000;

    // IT/AV Help Desk
    monthlyTotal += 1343.50;
  }

  console.log(`Monthly total for ${pkg}-${term}: ${monthlyTotal}`);
  return Math.round(monthlyTotal);
};


export const calculateAnnualTotal = (monthlyTotal: number): number => {
  return monthlyTotal * 12;
};

export const getAdjustedValue = (value: number, term: number): number => {
  const adjustmentFactor = 1 - (term - 2) * 0.1; // 10% decrease per year after year 2
  return value * adjustmentFactor;
};

export type ProfitData = {
  [year: string]: {
    [pkg: string]: number;
  };
};



export const formatPrice = (price: number): string => {
  const roundedPrice = Math.round(price);
  if (roundedPrice >= 1000000) {
    return `$<span class="text-6xl">${(roundedPrice / 1000000).toFixed(1)}</span>M`;
  } else {
    return `$<span class="text-5xl">${(roundedPrice / 1000).toFixed(0)}</span>k`;
  }
};


// Generate pricing data based on management costs and view mode
export const generatePricing = (isManagementMode: boolean, discountRate: number): Pricing => {
  const pricing: Pricing = {};
  [2, 3, 4, 5].forEach(term => {
    pricing[term.toString()] = {
      A: isManagementMode ? calculateNER('A', term, discountRate) : calculateMonthlyTotal('A', term),
      B: isManagementMode ? calculateNER('B', term, discountRate) : calculateMonthlyTotal('B', term),
      C: isManagementMode ? calculateNER('C', term, discountRate) : calculateMonthlyTotal('C', term)
    };
  });
  return pricing;
};

export const getCurrentPricing = (isManagementMode: boolean, discountRate: number): Pricing => {
  return generatePricing(isManagementMode, discountRate);
};
