import { Package, ManagementCosts } from './data';

const mockCalculateNER = (
  pkg: string, 
  term: number, 
  termPremium: number,
  managementCosts: ManagementCosts,
  packages: { [key: string]: Package }
): number => {
  // Base NER calculation
  let baseNER = 45 + (Math.random() * 5); // Random starting point between 45 and 50

  // Adjust NER based on term
  baseNER -= (term - 2) * 1; // $1 decrease for each year longer than 2 years

  // Adjust NER based on package
  if (pkg === 'B') {
    baseNER *= 1.15; // 15% higher for Package B
  } else if (pkg === 'C') {
    baseNER *= 1.30; // 30% higher for Package C
  }

  // Apply term premium increase
  baseNER *= (1 + (termPremium / 100));

  // Add some minor randomness (±$0.50)
  const randomFactor = (Math.random() - 0.5);
  baseNER += randomFactor;

  // Round to two decimal places
  return Math.round(baseNER * 100) / 100;
};

export default mockCalculateNER;
