import React, { useState, useEffect } from 'react';
import { packages, pricing, RSF, calculateMonthlyTotal, calculateAnnualTotal, getAdjustedValue, formatPrice, profitData, managementCosts } from './data';

type Package = {
  name: string;
  description: string;
  items: string[];
  params: {
    [key: string]: number;
  };
};

const FlipCard: React.FC<{ packageKey: string; packageData: Package; className?: string }> = ({ packageKey, packageData, className = '' }) => {
  const [isHovered, setIsHovered] = useState(false);

  return (
    <div 
      className={`box-standard relative border-4 border-[#A82229] flex items-center justify-center ${className}`}
      onMouseEnter={() => setIsHovered(true)}
      onMouseLeave={() => setIsHovered(false)}
    >
      <div className={`w-full h-full transition-opacity duration-300 ${isHovered ? 'opacity-0' : 'opacity-100'} flex flex-col items-center justify-center`}>
        <h3 className="text-7xl font-bold">{packageKey}</h3>
        <p className="text-lg">{packageData.name}</p>
      </div>
      <div className={`absolute top-0 left-0 w-full h-full bg-white p-1 overflow-auto transition-opacity duration-300 ${isHovered ? 'opacity-100' : 'opacity-0'}`}>
        <ul className="text-left text-sm leading-tight">
          {packageData.items.map((item, index) => (
            <li key={index} className="mb-1">{item}</li>
          ))}
        </ul>
      </div>
    </div>
  );
};

const SplitBox: React.FC<{ pkg: string; term: number; color: string; isSelected: boolean }> = ({ pkg, term, color, isSelected }) => (
  <div className={`w-full h-full relative ${isSelected ? 'bg-white' : color} ${isSelected ? 'text-[#A82229]' : 'text-white'}`}>
    <div className="absolute top-0 right-0 p-2">
      <div className="text-sm">INSIDE</div>
      <div className="text-sm">TERM</div>
    </div>
    <div className="absolute bottom-0 left-0 p-2">
      <div className="text-sm">OUTSIDE</div>
      <div className="text-sm">TERM</div>
    </div>
    <div className="absolute top-0 left-0 w-full h-full flex items-center justify-center">
      <div className={`transform rotate-45 w-[140%] h-0.5 ${isSelected ? 'bg-[#A82229]' : 'bg-white'}`}></div>
    </div>
  </div>
);

const PricingMatrix: React.FC<{
  selectedBox: {pkg: string, term: number} | null;
  setSelectedBox: React.Dispatch<React.SetStateAction<{pkg: string, term: number} | null>>;
  isManagementMode: boolean;
}> = ({ selectedBox, setSelectedBox, isManagementMode }) => {
  const [splitBoxes, setSplitBoxes] = useState<{[key: string]: boolean}>({});

  const isSelected = (pkg: string, term: number) => {
    return selectedBox?.pkg === pkg && selectedBox.term === term;
  };

  const getOpacity = (term: number) => {
    switch(term) {
      case 2: return 'opacity-50';
      case 3: return 'opacity-80';
      case 4: return 'opacity-100';
      case 5: return 'opacity-25';
      default: return 'opacity-100';
    }
  };

  const formatValue = (value: number) => {
    if (value >= 1000000) {
      return (
        <>
          <span className="text-2xl">$</span>
          <span className="text-5xl">{(value / 1000000).toFixed(1)}</span>
          <span className="text-2xl">M</span>
        </>
      );
    } else {
      return (
        <>
          <span className="text-2xl">$</span>
          <span className="text-5xl">{(value / 1000).toFixed(0)}</span>
          <span className="text-2xl">k</span>
        </>
      );
    }
  };

  const getColor = (term: number, pkg: string) => {
    const baseColors = ['#E6B3B3', '#D98B8B', '#CC6666'];
    const index = ['A', 'B', 'C'].indexOf(pkg);
    return baseColors[Math.min(term - 2, 2) * 3 + index];
  };

  const handleDoubleClick = (pkg: string, term: number) => {
    setSplitBoxes(prev => ({...prev, [`${pkg}-${term}`]: !prev[`${pkg}-${term}`]}));
  };

  return (
    <div className="grid grid-cols-[auto,1fr,1fr,1fr]">
      <div className="invisible mr-4 mb-8"></div>
      {Object.entries(packages).map(([key, value]) => (
        <FlipCard key={key} packageKey={key} packageData={value} className="mt-10 mb-10" />
      ))}
      {Object.entries(isManagementMode ? profitData : pricing).map(([term, values]) => (
        <React.Fragment key={term}>
          <div className={`box-standard flex justify-center items-center border-4 border-[#A82229] font-bold mr-12 mb-1 ${term === '5' ? 'opacity-25' : ''}`}>
            <div className="flex flex-col items-start">
              <div className="text-sm">Year</div>
              <div className="text-6xl mb-1">{term.padStart(2, '0')}</div>
            </div>
          </div>
          {Object.entries(values).map(([pkg, value], index) => {
            const selected = isSelected(pkg, parseInt(term));
            return (
              <button
                key={`${term}-${pkg}`}
                className={`box-standard flex items-center justify-center text-center
                  ${selected ? 'bg-white !text-[#A82229]' : 'bg-[#A82229] text-white'} text-2xl font-bold
                  border-4 border-[#A82229] ${selected ? 'border-8' : ''}
                  ${getOpacity(parseInt(term))}
                  transition-all duration-300 ${index < 2 ? 'mr-1' : ''} mb-1
                  hover:scale-95 hover:z-10`}
                onClick={() => setSelectedBox({pkg, term: parseInt(term)})}
                onDoubleClick={() => handleDoubleClick(pkg, parseInt(term))}
              >
                {splitBoxes[`${pkg}-${term}`] ? (
                  <SplitBox pkg={pkg} term={parseInt(term)} color={getColor(parseInt(term), pkg)} isSelected={selected} />
                ) : (
                  <div>
                    <span className="text-5xl">{formatValue(value)}</span>
                  </div>
                )}
              </button>
            );
          })}
        </React.Fragment>
      ))}
    </div>
  );
};

const LineItem: React.FC<{
  label: string;
  value: number | string;
  isWhole?: boolean;
  isBold?: boolean;
  isText?: boolean;
  isWhite?: boolean;
  opacity?: string;
}> = ({ label, value, isWhole = false, isBold = false, isText = false, isWhite = false, opacity = 'opacity-100' }) => (
  <div className={`flex justify-between ${isBold ? 'font-bold' : ''} ${opacity}`}>
    <div className={isWhite ? 'text-white' : 'text-[#A82229]'}>{label}</div>
    <div className={isWhite ? 'text-white' : 'text-gray-500'}>
      {isText ? (
        value
      ) : (
        <>
          $
          {typeof value === 'number'
            ? isWhole
              ? value.toLocaleString()
              : value.toFixed(2)
            : value}
        </>
      )}
    </div>
  </div>
);

const Sidebar: React.FC<{
  selectedBox: {pkg: string, term: number} | null;
  isExpanded: boolean;
  setIsExpanded: React.Dispatch<React.SetStateAction<boolean>>;
  isManagementMode: boolean;
  setIsManagementMode: React.Dispatch<React.SetStateAction<boolean>>;
}> = ({ selectedBox, isExpanded, setIsExpanded, isManagementMode, setIsManagementMode }) => {
  const [isFlipping, setIsFlipping] = useState(false);

  const toggleExpand = () => {
    if (!selectedBox) {
      setIsExpanded(!isExpanded);
    }
  };

  const toggleMode = () => {
    setIsFlipping(true);
    setTimeout(() => {
      setIsManagementMode(!isManagementMode);
      setIsFlipping(false);
    }, 300);
  };

  if (!selectedBox && !isExpanded) {
    return (
      <div 
        className="border-4 border-[#A82229] rounded-3xl p-6 text-[#A82229] cursor-pointer hover:bg-[#A82229] hover:text-white transition-colors duration-300"
        onClick={toggleExpand}
      >
        <div className="text-xl font-bold">160 Varick in Hudson</div>
      </div>
    );
  }

  const renderTenantView = () => {
    if (!selectedBox) {
      return (
        <>
          <div className="mb-4 text-xl font-bold">160 Varick in Hudson</div>
          <div className="flex justify-between mb-4">
            <div>0 yr</div>
          </div>
          <div className="flex justify-between mb-4 bg-[#A82229] text-white p-2 rounded-lg">
            <div className="font-bold">RSF:</div>
            <div className="text-2xl">{RSF.toLocaleString()}</div>
          </div>
        </>
      );
    } else {
      const {pkg, term} = selectedBox;
      const monthlyTotal = calculateMonthlyTotal(pkg, term);
      const annualTotal = calculateAnnualTotal(monthlyTotal);

      const getOpacity = (packageKey: string) => {
        switch(pkg) {
          case 'A': return packageKey === 'A' ? 'opacity-100' : 'opacity-20';
          case 'B': return packageKey === 'C' ? 'opacity-20' : 'opacity-100';
          case 'C': return 'opacity-100';
          default: return 'opacity-100';
        }
      };

      return (
        <>
          <div className="flex justify-between items-center mb-1">
            <div className="text-2xl font-bold">Tenant Name</div>
            <div className="flex items-center">
              <div className="bg-[#A82229] text-white rounded-full w-8 h-8 flex items-center justify-center mr-2 text-xl">
                {pkg}
              </div>
              <button className="bg-white text-[#A82229] border-2 border-[#A82229] px-4 py-1 rounded-full text-sm">Select</button>
            </div>
          </div>
          <div className="text-l mb-2 font-thin text-gray-400">{packages[pkg as keyof typeof packages].name}</div> 
          <div className="flex justify-between mb-4 text-lg">
            <div>160 Varick in Hudson</div>
            <div>{term} yr</div>
          </div>
          <div className="flex justify-between mb-6 bg-[#A82229] text-white p-2 rounded-lg">
            <div className="font-bold text-lg">RSF:</div>
            <div className="text-2xl">{RSF.toLocaleString()}</div>
          </div>
          {['A', 'B', 'C'].map((packageKey) => (
            <div key={packageKey} className={`mb-4 ${getOpacity(packageKey)}`}>
              {Object.entries(packages[packageKey as keyof typeof packages].params).map(([key, value]) => (
                <div key={key} className="flex justify-between mb-2">
                  <div>{key}</div>
                  <div>${getAdjustedValue(value, term).toFixed(2)}</div>
                </div>
              ))}
              {packageKey !== 'C' && <hr className="border-t border-[#A82229] my-4" />}
            </div>
          ))}
          <div className="mt-6 pt-4 border-t border-[#A82229]">
            <div className="flex justify-between font-bold text-lg">
              <div>Total Per SF</div>
              <div>${(annualTotal / RSF).toFixed(2)}</div>
            </div>
            <div className="flex justify-between font-bold text-lg bg-[#A82229] text-white p-2 mt-5 rounded-lg">
              <div>Total Monthly</div>
              <div>${monthlyTotal.toLocaleString(undefined, {maximumFractionDigits: 2})}</div>
            </div>
            <div className="flex justify-between font-bold text-lg bg-[#A82229] text-white p-2 mt-1 rounded-lg">
              <div>Total Annual</div>
              <div>${annualTotal.toLocaleString(undefined, {maximumFractionDigits: 2})}</div>
            </div>
          </div>
        </>
      );
    }
  };

  const renderManagementView = () => {
    const pkg = selectedBox?.pkg || 'C';
    const term = selectedBox?.term || 2;
    const profit = profitData[term.toString() as keyof typeof profitData][pkg as keyof (typeof profitData)[string]];

    const getOpacity = (packageKey: string) => {
      switch(pkg) {
        case 'A': return packageKey === 'A' ? 'opacity-100' : 'opacity-40';
        case 'B': return packageKey === 'C' ? 'opacity-40' : 'opacity-100';
        case 'C': return 'opacity-100';
        default: return 'opacity-100';
      }
    };

    const calculateTotal = () => {
      let total = 0;
      Object.values(managementCosts.preOccupancy).forEach(cost => {
        total += cost;
      });
      Object.values(managementCosts.recurring).forEach(cost => {
        total += cost * 12 * term;
      });
      return total;
    };

    return (
      <>
        <div className="flex justify-between items-center mb-1">
          <div className="text-2xl font-bold">Management Team</div>
          <div className="flex items-center">
            <div className="bg-[#A82229] text-white rounded-full w-8 h-8 flex items-center justify-center mr-2 text-xl">
              {pkg}
            </div>
            <button className="bg-white text-[#A82229] border-2 border-[#A82229] px-4 py-1 rounded-full text-sm">Select</button>
          </div>
        </div>
        <div className="text-l mb-2 font-thin italic text-gray-400">{packages[pkg as keyof typeof packages].name} Package</div>
        <div className="flex justify-between mb-4 text-lg">
          <div>160 Varick in Hudson</div>
          <div>{term} yr</div>
        </div>
        <div className="flex justify-between mb-6 bg-[#A82229] text-white p-2 rounded-lg">
          <div className="font-bold text-lg">RSF:</div>
          <div className="text-2xl">{RSF.toLocaleString()}</div>
        </div>
        <div className="mb-4 space-y-2">
          {Object.entries(managementCosts.preOccupancy).map(([key, value]) => (
            <LineItem key={key} label={key} value={value} opacity={getOpacity(key)} />
          ))}
        </div>
        <hr className="border-t-2 border-[#A82229] -mx-6" />
        <div className="my-4 space-y-2">
          {Object.entries(managementCosts.recurring).map(([key, value]) => (
            <LineItem key={key} label={key} value={value} isWhole={key !== 'Opex (monthly)'} opacity={getOpacity(key)} />
          ))}
        </div>
        <hr className="border-t-4 border-[#A82229] -mx-6" />
        <div className="my-5">
          <LineItem label="TOTAL" value={calculateTotal()} isBold />
        </div>
        <hr className="border-t-4 border-[#A82229] -mx-6" />

        <div className="bg-[#A82229] text-white p-3 rounded-lg mb-1 mt-5">
          <LineItem label="PROFIT" value={profit} isWhole isWhite />
        </div>
        <div className="bg-[#A82229] text-white p-3 mb-2 rounded-lg">
          <LineItem label="PAYBACK" value={`${Math.ceil(calculateTotal() / (profit / 12))} Months`} isText isWhite />
        </div>
      </>
    );
  };

  return (
    <div 
      className={`w-full transition-all duration-500 ease-in-out ${
        isExpanded ? 'h-[calc(100%-0px)]' : 'h-24'
      }`}
      onClick={toggleExpand}
    >
      <div 
        className={`border-4 border-[#A82229] rounded-3xl p-6 text-[#A82229] backface-hidden transition-all duration-600 ${
          isFlipping ? 'rotate-y-180' : ''
        } ${isExpanded ? 'h-full' : 'h-24 flex items-center justify-center'} overflow-hidden`}
      >
        {isExpanded ? (
          isManagementMode ? renderManagementView() : renderTenantView()
        ) : (
          <div className="text-xl font-bold">160 Varick in Hudson</div>
        )}
        {isExpanded && (
          <button 
            onClick={(e) => {
              e.stopPropagation();
              toggleMode();
            }}
            className="w-full bg-[#ffffff] border-2 border-[#A82229] text-[#A82229] py-2 rounded-full mt-4 hover:bg-[#ffffff] transition-colors duration-300"
          >
            {isManagementMode ? 'Switch to Tenant View' : 'Management View'}
          </button>
        )}
      </div>
    </div>
  );
};

const App: React.FC = () => {
  const [selectedBox, setSelectedBox] = useState<{pkg: string, term: number} | null>(null);
  const [isExpanded, setIsExpanded] = useState(false);
  const [isManagementMode, setIsManagementMode] = useState(false);

  useEffect(() => {
    if (selectedBox) {
      setIsExpanded(true);
    }
  }, [selectedBox]);

  return (
    <div className="p-8 font-['Inter'] font-black relative min-h-screen" style={{ color: '#A82229' }}>
      <h1 className="text-2xl mb-20 text-left">Flex Office Pricing Matrix (v.01)</h1>
      <div className="flex justify-between">
        <div className="w-[800px]">
          <PricingMatrix 
            selectedBox={selectedBox} 
            setSelectedBox={setSelectedBox} 
            isManagementMode={isManagementMode}
          />
        </div>
        <div className="w-[450px] flex flex-col">
          <div className={`transition-all duration-500 ease-in-out ${
            isExpanded ? '-mt-[-10px]' : '-mt-[-40px]'
          }`}>
            <Sidebar 
              selectedBox={selectedBox} 
              isExpanded={isExpanded} 
              setIsExpanded={setIsExpanded}
              isManagementMode={isManagementMode}
              setIsManagementMode={setIsManagementMode}
            />
          </div>
        </div>
      </div>
    </div>
  );
};

export default App;
