import React, { useState, useRef, useEffect } from 'react';
import './App.css';

type Package = {
  name: string;
  description: string;
  items: string[];
  params: {
    [key: string]: number;
  };
};

type Pricing = {
  [year: number]: {
    [packageKey: string]: number;
  };
};

const packages: { [key: string]: Package } = {
  A: {
    name: 'Flexible',
    description: 'Flexible',
    items: ['Flexible Terms', 'Enterprise Network Connectivity', 'Day 1 Branding & Signage', 'Core Occupancy Data'],
    params: {
      'Term Rent': 156,
      'Network WiFi': 20,
      'Branding and Signage': 15,
      'Base Rent': 69,
    }
  },
  B: {
    name: 'Inclusive',
    description: 'Inclusive',
    items: ['Furnished', 'Full Digital Infrastructure', 'AV Services Hardware', 'Plant Programming', 'Food & Beverage Service'],
    params: {
      'Furniture': 24,
      'Full Digital Infrastructure': 12,
      'Plants Programming': 15,
      'Food & Beverage Service': 61,
    }
  },
  C: {
    name: 'Fully Managed',
    description: 'Fully Managed',
    items: ['Dedicated WX Coordinator', 'Full Mail and Package Handling', 'Hardware & Data Collection', 'IT/AV Help Desk'],
    params: {
      'Dedicated WX': 34,
      'Package Handling': 66,
      'Hardware & Data': 52,
      'IT/AV Help Desk': 22,
    }
  }
};

const pricing: Pricing = {
  2: { A: 100000, B: 120000, C: 140000 },
  3: { A: 80000, B: 100000, C: 120000 },
  4: { A: 60000, B: 80000, C: 100000 },
  5: { A: 60000, B: 80000, C: 100000 }
};

const FlipCard: React.FC<{ packageKey: string; packageData: Package }> = ({ packageKey, packageData }) => {
  const [isFlipped, setIsFlipped] = useState(false);

  return (
    <div
      className="flip-card w-full h-full"
      onMouseEnter={() => setIsFlipped(true)}
      onMouseLeave={() => setIsFlipped(false)}
    >
      <div className={`flip-card-inner ${isFlipped ? 'flipped' : ''}`}>
        <div className="flip-card-front border-4 border-[#A82229] p-2 text-center flex flex-col justify-center items-center">
          <h3 className="text-4xl font-bold">{packageKey}</h3>
          <p className="text-sm">{packageData.name}</p>
        </div>
        <div className="flip-card-back border-4 border-[#A82229] p-2 text-left overflow-auto">
          <ul className="text-xs">
            {packageData.items.map((item, index) => (
              <li key={index} className="mb-1">{item}</li>
            ))}
          </ul>
        </div>
      </div>
    </div>
  );
};

interface SidebarProps {
  selectedPackage: string | null;
  selectedTerm: number | null;
  position: { x: number; y: number };
  setPosition: React.Dispatch<React.SetStateAction<{ x: number; y: number }>>;
}

const Sidebar = React.forwardRef<HTMLDivElement, SidebarProps>(
  ({ selectedPackage, selectedTerm, position, setPosition }, ref) => {
    const [isDragging, setIsDragging] = useState(false);
    const [dragStart, setDragStart] = useState({ x: 0, y: 0 });

    const handleMouseDown = (e: React.MouseEvent) => {
      setIsDragging(true);
      setDragStart({ x: e.clientX - position.x, y: e.clientY - position.y });
    };

    const handleMouseMove = (e: MouseEvent) => {
      if (isDragging) {
        setPosition({
          x: e.clientX - dragStart.x,
          y: e.clientY - dragStart.y
        });
      }
    };

    const handleMouseUp = () => {
      setIsDragging(false);
    };

    useEffect(() => {
      document.addEventListener('mousemove', handleMouseMove);
      document.addEventListener('mouseup', handleMouseUp);
      return () => {
        document.removeEventListener('mousemove', handleMouseMove);
        document.removeEventListener('mouseup', handleMouseUp);
      };
    }, [isDragging, dragStart]);

    if (!selectedPackage || !selectedTerm) {
      return null;
    }

    const price = pricing[selectedTerm][selectedPackage];

    const allParams = {
      ...packages.A.params,
      ...packages.B.params,
      ...packages.C.params
    };

    return (
      <div
        ref={ref}
        className="sidebar"
        style={{ left: position.x, top: position.y }}
        onMouseDown={handleMouseDown}
      >
        <div className="header">
          <div className="text-2xl font-bold">{packages[selectedPackage].name}</div>
          <div className="flex items-center">
            <div className="package">
              {selectedPackage}
            </div>
            <button>Select</button>
          </div>
        </div>
        <div className="details">
          <div>160 Varick in Hudson</div>
          <div>{selectedTerm} yr</div>
        </div>
        <div className="details">
          <div className="font-bold">RSF:</div>
          <div className="text-2xl rsf">5,374</div>
        </div>
        {Object.entries(allParams).map(([key, value], index, array) => (
          <React.Fragment key={key}>
            <div className={`details ${
              (selectedPackage === 'A' && packages.A.params[key] === undefined) ||
              (selectedPackage === 'B' && packages.A.params[key] === undefined && packages.B.params[key] === undefined)
                ? 'opacity-20'
                : ''
            }`}>
              <div>{key}</div>
              <div>${value}</div>
            </div>
            {(index === Object.keys(packages.A.params).length - 1 ||
              index === Object.keys(packages.A.params).length + Object.keys(packages.B.params).length - 1) &&
              <hr className="border-t border-[#A82229] my-4" />}
          </React.Fragment>
        ))}
        <div className="pricing">
          <div className="flex justify-between font-bold">
            <div>TOTAL PER SF</div>
            <div>$234</div>
          </div>
          <div className="total">
            <div>TOTAL MONTHLY</div>
            <div>${(price / 12).toLocaleString()}</div>
          </div>
          <div className="total">
            <div>TOTAL ANNUAL</div>
            <div>${price.toLocaleString()}</div>
          </div>
        </div>
      </div>
    );
  }
);

const App: React.FC = () => {
  const [selectedPackage, setSelectedPackage] = useState<string | null>(null);
  const [selectedTerm, setSelectedTerm] = useState<number | null>(null);
  const [sidebarPosition, setSidebarPosition] = useState({ x: 0, y: 0 });
  const sidebarRef = useRef<HTMLDivElement>(null);

  const handleSelection = (pkg: string, term: number) => {
    setSelectedPackage(pkg);
    setSelectedTerm(term);
  };

  const getOpacity = (term: number) => {
    switch(term) {
      case 2: return 'opacity-50';
      case 3: return 'opacity-80';
      case 4: return 'opacity-100';
      case 5: return 'opacity-15';
      default: return 'opacity-100';
    }
  };

  return (
    <div className="flex flex-col lg:flex-row p-4">
      <div className="w-full lg:w-1/3 mb-4 lg:mb-0 lg:mr-4">
        <h1 className="text-2xl mb-4">Office Leasing Matrix</h1>
        <div className="border-2 border-[#A82229] p-4 mb-4 text-[#A82229]">
          <h2 className="text-xl mb-2">Packages</h2>
          <div className="packages">
            {Object.entries(packages).map(([key, pkg]) => (
              <FlipCard key={key} packageKey={key} packageData={pkg} />
            ))}
          </div>
        </div>
        <div className="border-2 border-[#A82229] p-4 text-[#A82229]">
          <h2 className="text-xl mb-2">Terms</h2>
          <div className="grid grid-cols-1 gap-2">
            {Object.keys(pricing).map((year) => (
              <div
                key={year}
                className={`package cursor-pointer ${+year === selectedTerm ? 'selected' : ''}`}
                onClick={() => handleSelection(selectedPackage || 'A', +year)}
              >
                <h3 className="text-2xl font-bold">{year} Year</h3>
                <p>Pricing for {year} years</p>
              </div>
            ))}
          </div>
        </div>
      </div>
      <div className="w-full lg:w-2/3">
        <h2 className="text-2xl mb-4">Pricing Matrix</h2>
        <div className="custom-grid">
          {Object.keys(pricing).map((term) =>
            Object.keys(packages).map((pkg) => (
              <div key={`${term}-${pkg}`} className={`cellClass ${getOpacity(+term)}`}>
                <div
                  className={`contentClass border-4 border-[#A82229] cursor-pointer ${pkg === selectedPackage && +term === selectedTerm ? 'bg-[#A82229] text-white' : 'bg-white text-[#A82229]'} rounded-lg`}
                  onClick={() => handleSelection(pkg, +term)}
                >
                  <div>
                    <h3 className="text-2xl font-bold">{pkg}</h3>
                    <p className="text-lg">${(pricing[+term][pkg] / 12).toLocaleString()}</p>
                  </div>
                </div>
              </div>
            ))
          )}
        </div>
      </div>
      {selectedPackage && selectedTerm && (
        <Sidebar
          ref={sidebarRef}
          selectedPackage={selectedPackage}
          selectedTerm={selectedTerm}
          position={sidebarPosition}
          setPosition={setSidebarPosition}
        />
      )}
    </div>
  );
};

export default App;
