const App: React.FC = () => {
  const [selectedBox, setSelectedBox] = useState<{pkg: string, term: number} | null>(null);
  const [columnGap, setColumnGap] = useState<number>(8);

  return (
    <div className="p-8 font-['Inter'] font-black relative min-h-screen" style={{ color: '#A82229' }}>
      <h1 className="text-3xl mb-12 text-left">Real Time Pricing Matrix</h1>
      <div className="flex justify-between">
        <div style={{ width: '800px' }}>
          <PricingMatrix selectedBox={selectedBox} setSelectedBox={setSelectedBox} columnGap={columnGap} />
        </div>
        <div className="w-4" /> {/* Spacer */}
        <div style={{ width: '400px' }}>
          <Sidebar selectedBox={selectedBox} />
        </div>
      </div>
      <div className="mt-8">
        <label htmlFor="gap-slider" className="block mb-2">Adjust Column Gap:</label>
        <input
          id="gap-slider"
          type="range"
          min="0"
          max="50"
          value={columnGap}
          onChange={(e) => setColumnGap(Number(e.target.value))}
          className="w-full"
        />
        <div className="text-center mt-2">{columnGap}px</div>
      </div>
    </div>
  );
};
